    "dashboard.naverapp.guide.step.2.7.contents": "Register <span class='guide_bold'>Web service URL</span> from the Register service environment.",
    "dashboard.naverapp.guide.step.2.7.1.contents": "Add http://creatorlink.net",
    "dashboard.naverapp.guide.step.2.7.1.contents.gabia": "Add http://creatorlink-gabia.com",
