    "dashboard.socialLogin.google.guide.title":       "Google Login Integration Guide",   
    "dashboard.socialLogin.google.guide.step.0":      "1.Create a Project",    
    "dashboard.socialLogin.google.guide.step.1":      "2.Create an App",
    "dashboard.socialLogin.google.guide.step.2":      "3.Google Login Activation",
    "dashboard.socialLogin.google.guide.step.m.0":      "Step 1",    
