/**
* setLanguage 
* use $.lang[currentLanguage][languageNumber]
*/
function setLanguage(currentLanguage) {
    $('[data-lang]').each(function() {
        var $this = $(this),
            replace_str = $.lang[currentLanguage][$this.data('lang')];

        if( typeof replace_str != 'undefined' &&
            replace_str.indexOf(':') === 0 &&
            $(this).closest('.user-logs').length > 0
        ) {
            replace_str = replace_str.replace(/^: /,'');
        }

        $this.html(replace_str); 
    });

    $('input[data-placeholder-lang]').each(function() {
        var $input = $(this),
            replace_k = $input.attr('data-placeholder-lang'),
            replace_v = $.lang[currentLanguage][replace_k];
        $input.attr('placeholder',replace_v);
    });
}

function getLanguage(c) {
    var lang = (typeof c == 'undefined') ? $.cookie('ci_sitelang') : '';
    if(!lang) lang = navigator.language || navigator.userLanguage;
    switch(lang) {
        case "ko":
        case "ko-KR":
        case "korean":
            lang = "ko";
            break;
        case "en":
        case "en-US":
        case "english":
            lang = "en";
            break;
        case "ja":
            lang = "ja";
            break;
        default:
            lang = "ko";
            break;
    }
    return lang;    
}

var getKeyByValue = function( array, value ) {
    for( var prop in array ) {
        if( array.hasOwnProperty( prop ) ) {
            //console.log(array[ prop ]);
            if( array[ prop ].toUpperCase() == value.toUpperCase() )
                return prop;
        }
    }
    return "";
}
